    @Test
    fun testCodec() {
        data.forEach {
            val decodeFromString = Json.decodeFromString<JsonObject>(it)
            val entities = decodeFromString["initialState"]!!.jsonObject["entities"]!!.jsonObject
            val questions = entities["questions"]!!.jsonObject
            questions.forEach { (_, it) ->
                Json.decodeFromJsonElement<DataHolder.Question>(it)
            }
            val answers = entities["answers"]!!.jsonObject
            answers.forEach { (_, it) ->
                Json.decodeFromJsonElement<DataHolder.Answer>(it)
            }
            val articles = entities["articles"]!!.jsonObject
            articles.forEach { (_, it) ->
                AccountData.decodeJson<DataHolder.Article>(it)
            }
        }
    }

